<?php/** * Вывод ответа */function output($error = null, $result = null){    $response = array('success' => empty($error), 'Error_Msg' => $error, 'result' => $result);    exit(json_encode($response));}$content = '';if (isset($_POST['content'])) {    $content = strip_tags($_POST['content']);    $content = trim($content);	$content = mb_ereg_replace('\n', '<br />', $content);}if (empty($content)) {    output('Нужно вставить текст!');} else {    output(null, $content);}?>